import React from "react";
import { useSelector } from "react-redux";

const Info = () => {
  const game = useSelector((state) => state.game);

  console.log("info", game);

  // useEffect(() => {
  console.log("zmiana info", game);
  console.log("game.pasue", game.pause);
  console.log("game.gameStatus", game.gameStatus);
  // eslint-disable-next-line
  // }, [game]);

  return (
    <div>
      {game.pause === true ? <p>Game paused</p> : <></>}
      {game.gameStatus === "START" && (
        <p>Choose three positions to find treasures!</p>
      )}
      {game.moves === 2 && (
        <div>
          <p>Not bad! Try again to find all treasures!</p>
          <ul>
            <li> T - Congrat! It's treasure! </li>
            <li> 3 - It's reeeeeealy close! </li>
            <li> 2 - You are close. Try again! </li>
            <li> 1 - Looking for treasure a little further.</li>
            <li> 0 - Oh! It's not even close.. </li>
          </ul>
        </div>
      )}
      {game.gameStatus === "END" && <p>Woohoo! You found all treasures!</p>}
    </div>
  );
};

