const initStateGame = {
  player: null,
  pause: false,
  gameStatus: null,
  moves: 1,
  treasures: [],
  findedTreasures: 0,
  scores: [],
};

const gameReducer = (state = initStateGame, action) => {
  switch (action.type) {
    case "START":
      return {
        ...state,
        gameStatus: action.status,
        player: action.name,
        treasures: action.treasures,
        findedTreasures: 0,
        moves: 1,
      };
    case "AFTERMOVE":
      return {
        ...state,
        gameStatus: action.status,
        moves: state.moves + 1,
        findedTreasures: state.findedTreasures + 1,
      };
    case "END":
      return {
        ...state,
        gameStatus: action.status,
      };
    case "PAUSE":
      return {
        ...state,
        pause: action.pause,
      };
    case "PLAYAGAIN":
      return {
        ...state,
        pause: action.pause,
      };
    case "NEXTGAME":
      return {
        ...state,
        gameStatus: action.status,
        moves: 1,
        findedTreasures: 0,
      };
    default:
      return state;
  }
};

export default gameReducer;
