const initStateSelect = {
  selectFields: [],
};

const selectFieldsReducer = (state = initStateSelect, action) => {
  switch (action.type) {
    case "ADD_SELECT_FIELD":
      return {
        ...state,
        selectFields: [...state.selectFields, action.field],
      };
    case "RESET_SELECT_FIELD":
      return { selectFields: [] };
    default:
      return state;
  }
};

export default selectFieldsReducer;
