const initStateScore = [
  { name: "Magda", score: 4 },
  { name: "Karol", score: 3 },
];

const scoreReducer = (state = initStateScore, action) => {
  switch (action.type) {
    case "ADD_SCORE":
      return [...state, action.score];
    default:
      return state;
  }
};

export default scoreReducer;
