const initStateScore = [
  { name: "Magda", score: 4 },
  { name: "Karol", score: 3 },
  { name: "Karol", score: 10 },
  { name: "Karol", score: 2 },
  { name: "Paweł", score: 1 },
];

const scoreReducer = (state = initStateScore, action) => {
  switch (action.type) {
    case "ADD_SCORE":
      return [...state, action.score];
    default:
      return state;
  }
};

export default scoreReducer;
