const initStateScore = [
  { score: 4, name: "Magda" },
  { score: 3, name: "Karol" },
  { score: 10, name: "Karol" },
  { score: 2, name: "Karol" },
  { score: 1, name: "Paweł" },
];

const scoreReducer = (state = initStateScore, action) => {
  switch (action.type) {
    case "ADD_SCORE":
      return [...state, action.score];
    default:
      return state;
  }
};

export default scoreReducer;
