const initStateUncovered = ["uncover"];

const uncoveredFieldsReducer = (state = initStateUncovered, action) => {
  switch (action.type) {
    case "ADD_UNCOVERED_FIELD":
      return [...state, action.field];
    case "RESET_UNCOVERED_FIELD":
      return [];
    default:
      return state;
  }
};

export default uncoveredFieldsReducer;
