import React, { useEffect } from "react";
import { useSelector } from "react-redux";

const Info = () => {
  const game = useSelector((state) => state.game);

  console.log("info", game);

  useEffect(() => {
    console.log("zmiana info", game);
    // eslint-disable-next-line
  }, [game]);

  return (
    <div>
      {game.pause === false ? (
        (game.gameStatus === "START" && (
          <p>Choose three positions to find treasures!</p>
        ),
        game.gameStatus === "AFTERMOVE" && (
          <div>
            <p>Not bad! Try three other ones to find all treasures!</p>

            <p> T - Congrat! It's treasure! </p>
            <p> 3 - It's reeeeeealy close! </p>
            <p> 2 - You are close. Try again! </p>
            <p> 1 - Looking for treasure a little further.</p>
            <p> 0 - Oh! It's not even close.. </p>
          </div>
        ),
        game.gameStatus === "END" && <p>Woohoo! You found all treasures!</p>)
      ) : (
        <p>Game paused</p>
      )}
    </div>
  );
};

