const initStateUncovered = [
  // { id: "22", find: "skarb" },
  // { id: "23", find: "3" },
  // { id: "55", find: "0" },
];

const uncoveredFieldsReducer = (state = initStateUncovered, action) => {
  switch (action.type) {
    case "ADD_UNCOVER_FIELD":
      return;
      [...state, action.field];
    default:
      return state;
  }
};

export default uncoveredFieldsReducer;
